sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;

	$scope.clickButton = function(x) {
// update server object with info from client script		
		c.data.answer = x; 
		// console.log(x);
// run the server again, populates the input object		
		c.server.update(); 
	}
};

